mapper.select:
SELECT COUNT(ID)
FROM IAAP_CM_DIRECTORY_GROUP
WHERE USE_YN = 'Y'

    	AND PATH_GUBUN = #{gubunType}


    AND NAME LIKE CONCAT('%', #{name}, '%')


    AND ID = #{id}

SELECT DIR_GROUP.ID, DIR_GROUP.NAME, DIR_GROUP.DESCRIPTION, DIR_GROUP.HOME_PATH path, DIR_GROUP.PATH_GUBUN gubun, DIR_GROUP.REG_DT FROM IAAP_CM_DIRECTORY_GROUP DIR_GROUP WHERE DIR_GROUP.USE_YN = 'Y'
AND PATH_GUBUN = #{gubunType}
AND DIR_GROUP.NAME LIKE CONCAT('%', #{name}, '%')
AND DIR_GROUP.ID = #{id}
ORDER BY DIR_GROUP.REG_DT DESC LIMIT #{size} OFFSET #{start};

mapper.update:
UPDATE IAAP_CM_DIRECTORY_GROUP
SET USE_YN = 'N'
WHERE ID = #{id}
