mapper.select:
SELECT COUNT(ID)
FROM IAAP_CM_DIRECTORY
WHERE USE_YN = 'Y'
AND GROUP_ID = #{groupId}
AND NAME = #{name}

SELECT COUNT(ID)
FROM IAAP_CM_DIRECTORY
WHERE USE_YN = 'Y'
AND GROUP_ID = #{groupId}
AND ID != #{excludedDirId}
AND NAME = #{name}

SELECT COUNT(ID)
FROM IAAP_CM_DIRECTORY
WHERE USE_YN = 'Y'
AND ID != #{excludedDirId}
AND PATH = #{path}

mapper.update:
UPDATE IAAP_CM_DIRECTORY
SET IN_USE = #{inUse}
WHERE ID = #{id}

UPDATE IAAP_CM_DIRECTORY
SET
    NAME = #{name},
    PATH = #{path}
WHERE ID = #{id}

UPDATE IAAP_CM_DIRECTORY
SET PRIORITY = #{priority}
WHERE ID = #{id}

UPDATE IAAP_CM_DIRECTORY
SET USE_YN = 'N'
WHERE ID = #{id}
