mapper.insert:
		/* SttErrorMapper.insert */
        INSERT INTO 
        	IAAP_STT_ERROR_LOG 
        	(
        		ID,
				TYPE,
				ERROR_CODE,
				ERROR_MSG,
				API_URL,
				SERVER_ID,
				ERROR_POINT,
				THRESHOLD,
				STATUS_VALUE,
				ERROR_TIME
			)
        VALUES

        (
        	NEXTVAL(SEQ_IAAP_STT_ERROR_LOG)
        	, #{item.type}

				, #{item.errorCode}
					,''


				, #{item.errorMsg}
					,''


				, #{item.apiUrl}
					,''


				, #{item.serverId}
					,''


				, #{item.errorPoint}
					,''


				, #{item.threshold}
					,0


				, #{item.statusValue}
					,0

            , NOW()
        )

mapper.select:
/* SttErrorMapper.list */ SELECT ID, DATE_FORMAT(ERROR_TIME, '%Y-%m-%d %H:%i') AS errorTime, TYPE, CASE WHEN TYPE = 'IF' THEN ERROR_CODE ELSE CASE WHEN ERROR_POINT = 'C' THEN 'CPU' WHEN ERROR_POINT = 'M' THEN 'MEMORY' WHEN ERROR_POINT = 'S' THEN 'STORAGE' END END AS code, CASE WHEN TYPE = 'IF' THEN ERROR_MSG ELSE CONCAT(STATUS_VALUE,'% (',THRESHOLD,'%)') END AS contents, CASE WHEN TYPE = 'IF' THEN API_URL ELSE '' END AS etc FROM IAAP_STT_ERROR_LOG WHERE 1=1
AND TYPE = #{type}
AND ERROR_TIME >= #{from}
AND ERROR_TIME <= DATE(#{to}) + 1
ORDER BY ERROR_TIME DESC
