mapper.select:
	/* SttTestMapper.detail */
        SELECT

        FROM
            IAAP_STT_TEST_DATA AS ST
        WHERE
            ST.UUID = #{uuid}

mapper.insert:
	/* SttTestMapper.insert */
        INSERT INTO IAAP_STT_TEST_DATA (uuid
                                       , result_code
                                       , result_msg
                                       , service_code
                                       , stt_result)
        VALUES(#{uuid}
              , #{resultCode}
              , #{resultMsg}
              , #{serviceCode}
              , #{sttResult, typeHandler=com.kt.smp.stt.test.handler.SttTestResultTypeHandler});

mapper.delete:
	/* SttTestMapper.delete */
        DELETE FROM
            IAAP_STT_TEST_DATA
        WHERE
            uuid = #{uuid}
