mapper.select:
/* SttTrainMapper.list */ SELECT
FROM IAAP_STT_TRAIN_MNG as TM WHERE 1 = 1 AND TM.USE_YN = 'Y'
AND TM.SERVICE_MODEL_ID = #{serviceModelId}
AND TM.UPDATED_BY LIKE CONCAT(#{updatedBy}, '%')
AND TM.STATUS = #{status}
AND TM.REQUESTED_AT >= CONCAT(#{from}, ' ', '00:00:00')
AND TM.REQUESTED_AT >= '2022-01-01 00:00:00'
AND TM.REQUESTED_AT <= CONCAT(#{to}, ' ', '23:59:59')
AND TM.REQUESTED_AT <= NOW()
AND TM.DESCRIPTION LIKE CONCAT('%', #{description}, '%')

		/* SttTrainMapper.count */
        SELECT COUNT(1)
            FROM IAAP_STT_TRAIN_MNG as TM
        WHERE
        1 = 1
            AND TM.USE_YN = 'Y'

            AND TM.SERVICE_MODEL_ID = #{serviceModelId}


            AND TM.CREATED_BY = #{createdBy}


            AND TM.STATUS = #{status}



                AND TM.REQUESTED_AT >= CONCAT(#{from}, ' ', '00:00:00')


                AND TM.REQUESTED_AT >= '2022-01-01 00:00:00'





                AND TM.REQUESTED_AT <= CONCAT(#{to}, ' ', '23:59:00')




                AND TM.REQUESTED_AT <= NOW()

mapper.insert:
		/* SttTrainMapper.insert */
        INSERT INTO IAAP_STT_TRAIN_MNG (id
                                        , service_model_id
                                        , description
                                        , updated_by
                                        , requested_at
                                        , completed_at
                                        , duration
                                        , data_num
                                        , result_model_id
                                        , result_code
                                        , result_msg
                                        , status
                                        , model_type
                                        , model_path
                                        , model_auth_key
                                        , train_data_path
                                        , REG_DT
                                        , REG_ID
                                        , REG_IP
                                        , UPD_DT
                                        , UPD_ID
                                        , UPD_IP
                                        , USE_YN
                                        , E2ESL_DATAPATH_LIST
                                        , E2ESL_WAVPATH_LIST
                                        , E2EUSL_WAVPATH_LIST
                                        , AM_DATASET_LIST
                                        )
        VALUES(NEXTVAL(SEQ_IAAP_STT_TRAIN_MNG)
              , #{serviceModelId}
              , #{description}
              , #{updatedBy}
              , NOW()
              , #{completedAt}
              , #{duration}
              , #{dataNum}
              , #{resultModelId}
              , #{resultCode}
              , #{resultMsg}
              , #{status}
              , #{modelType}
              , #{modelPath}
              , #{modelAuthKey}
              , #{trainDataPath}
              , NOW()
              , #{regId}
              , #{regIp}
              , NOW()
              , #{updId}
              , #{updIp}
              , 'Y'
              , #{trainE2ESLDataPathList, typeHandler=com.kt.smp.stt.train.train.handler.SttTrainProcessHandler}
              , #{trainE2ESLWavPathList, typeHandler=com.kt.smp.stt.train.train.handler.SttTrainProcessHandler}
              , #{trainE2EUSLWavPathList, typeHandler=com.kt.smp.stt.train.train.handler.SttTrainProcessHandler}
              , #{trainAmDatasetList, typeHandler=com.kt.smp.stt.train.train.handler.SttTrainProcessHandler}
              );

mapper.update:
		/* SttTrainMapper.update */
        UPDATE
            IAAP_STT_TRAIN_MNG
        SET
            DESCRIPTION = #{description}
          , UPD_DT = NOW()
          , UPD_ID = #{updId}
          , UPD_IP = #{updIp}

          , REQUESTED_AT = NOW()


          , DATA_NUM = #{dataNum}


          , DATA_TIME = #{dataTime}


          , RESULT_CODE = #{resultCode}


          , RESULT_MSG = #{resultMsg}


          , COMPLETED_AT = NOW()


          , DURATION = TIMESTAMPDIFF(second, #{requestedAt}, NOW())


          , STATUS = #{status}


          , MODEL_TYPE = #{modelType}


          , MODEL_PATH = #{modelPath}


          , MODEL_AUTH_KEY = #{modelAuthKey}

        WHERE
            ID = #{id}
        AND
            USE_YN = 'Y'

/* SttTrainMapper.detailUpdate */
UPDATE
  IAAP_STT_TRAIN_MNG
SET
  DESCRIPTION = #{description}
WHERE ID = #{id}
AND USE_YN = 'Y'
