mapper.select:
/* VerifyDatasetMapper.search */ SELECT DATASET.ID, SERVICE_MODEL.SERVICE_MODEL_NAME, DATASET.NAME, ( SELECT COUNT(DATA.ID) FROM IAAP_STT_VERIFY_DATA DATA WHERE DATA.USE_YN = 'Y' AND DATA.DATASET_ID = DATASET.ID ) NUM_OF_DATA, DATASET.DESCRIPTION, DATASET.REG_ID, DATASET.REG_DT FROM IAAP_STT_VERIFY_DATASET DATASET JOIN IAAP_STT_SERVICE_MODEL SERVICE_MODEL ON SERVICE_MODEL.SERVICE_CODE = DATASET.SERVICE_MODEL_ID WHERE DATASET.USE_YN = 'Y'
AND DATASET.SERVICE_MODEL_ID = #{serviceModelId}
AND DATASET.NAME LIKE CONCAT('%', #{name}, '%')
AND DATASET.REG_ID LIKE CONCAT('%', #{regId}, '%')
AND DATASET.REG_DT >= CONCAT(#{regDtFrom}, ' ', '00:00:00')
AND DATASET.REG_DT >= '2022-01-01 00:00:00'
AND DATASET.REG_DT <= CONCAT(#{regDtTo}, ' ', '23:59:59')
AND DATASET.REG_DT <= NOW()
ORDER BY DATASET.REG_DT DESC LIMIT #{size} OFFSET #{start};

		/* VerifyDatasetMapper.countByName */
        SELECT COUNT(ID)
        FROM IAAP_STT_VERIFY_DATASET
        WHERE USE_YN = 'Y'
        AND NAME = #{name}

		/* VerifyDatasetMapper.countByDirectory */
        SELECT COUNT(ID)
        FROM IAAP_STT_VERIFY_DATASET
        WHERE USE_YN = 'Y'
        AND DIRECTORY_ID = #{directoryId}

mapper.update:
		/* VerifyDatasetMapper.update */
        UPDATE IAAP_STT_VERIFY_DATASET
        SET
            NAME = #{name},
            DESCRIPTION = #{description},
            UPD_ID = #{updId},
            UPD_IP = #{updIp}
        WHERE ID = #{id}

		/* VerifyDatasetMapper.delete */
        UPDATE IAAP_STT_VERIFY_DATASET
        SET USE_YN = 'N'
        WHERE ID = #{id}
