mapper.select:
/* DictationMapper.count */ SELECT COUNT(DICTATION.ID) FROM IAAP_STT_DICTATION DICTATION JOIN IAAP_STT_SERVICE_MODEL SERVICE_MODEL ON SERVICE_MODEL.SERVICE_CODE = TO_CHAR(DICTATION.SERVICE_MODEL_ID) WHERE DICTATION.USE_YN = 'Y'
AND DICTATION.SERVICE_MODEL_ID = #{serviceModelId}
AND DICTATION.USAGE_TYPE = #{usageType}
AND DICTATION.WAV_FILE_PATH LIKE '%' || #{wavFileName} || '%'
AND DICTATION.DICTATED_TEXT LIKE '%' || #{dictatedText} || '%'
AND DICTATION.UPD_ID LIKE '%' || #{updId} || '%'
AND DICTATION.REG_DT >= TO_DATE(#{regDtFrom} || ' ' || '00:00:00', 'YY-MM-DD HH24:MI:SS')
AND DICTATION.REG_DT >= TO_DATE('2022-01-01 00:00:00','YY-MM-DD HH24:MI:SS')
AND DICTATION.REG_DT <= TO_DATE(#{regDtTo} || ' ' || '23:59:59', 'YY-MM-DD HH24:MI:SS')
AND DICTATION.REG_DT <= current_date

/* DictationMapper.search */ SELECT ID, WAV_FILE_PATH, SERVICE_MODEL_NAME, TRANSCRIPT, DICTATED_TEXT, USAGE_TYPE, REG_ID, REG_DT, UPD_ID, UPD_DT, IS_PREEMPTED FROM( SELECT ID, WAV_FILE_PATH, SERVICE_MODEL_NAME, TRANSCRIPT, DICTATED_TEXT, USAGE_TYPE, REG_ID, REG_DT, UPD_ID, UPD_DT, IS_PREEMPTED, ROWNUM AS RNUM FROM ( SELECT DICTATION.ID, DICTATION.WAV_FILE_PATH, SERVICE_MODEL.SERVICE_MODEL_NAME, DICTATION.TRANSCRIPT, DICTATION.DICTATED_TEXT, DICTATION.USAGE_TYPE, DICTATION.REG_ID, DICTATION.REG_DT, DICTATION.UPD_ID, DICTATION.UPD_DT, DICTATION.IS_PREEMPTED FROM IAAP_STT_DICTATION DICTATION JOIN IAAP_STT_SERVICE_MODEL SERVICE_MODEL ON SERVICE_MODEL.SERVICE_CODE = TO_CHAR(DICTATION.SERVICE_MODEL_ID) WHERE DICTATION.USE_YN = 'Y'
AND DICTATION.SERVICE_MODEL_ID = #{serviceModelId}
AND DICTATION.USAGE_TYPE = #{usageType}
AND DICTATION.WAV_FILE_PATH LIKE '%' || #{wavFileName} || '%'
AND DICTATION.DICTATED_TEXT LIKE '%' || #{dictatedText} || '%'
AND DICTATION.UPD_ID LIKE '%' || #{updId} || '%'
AND DICTATION.REG_DT >= TO_DATE(#{regDtFrom} || ' ' || '00:00:00', 'YY-MM-DD HH24:MI:SS')
AND DICTATION.REG_DT >= TO_DATE('2022-01-01 00:00:00','YY-MM-DD HH24:MI:SS')
AND DICTATION.REG_DT <= TO_DATE(#{regDtTo} || ' ' || '23:59:59','YY-MM-DD HH24:MI:SS')
AND DICTATION.REG_DT <= current_date
ORDER BY DICTATION.REG_DT DESC ) ) WHERE 1=1
AND RNUM > #{start} AND RNUM < #{start}+1 + #{size}

		/* DictationMapper.countByWavFilePath */
        SELECT COUNT(ID)
        FROM IAAP_STT_DICTATION
        WHERE USE_YN = 'Y'
        AND WAV_FILE_PATH = #{wavFilePath}

		/* DictationMapper.countByDeprecatedWavFilePath */
        SELECT COUNT(ID)
        FROM IAAP_STT_DICTATION
        WHERE USE_YN = 'N'
        AND WAV_FILE_PATH = #{wavFilePath}

		/* DictationMapper.findUseYnBySttLogId */
        SELECT USE_YN
        FROM IAAP_STT_DICTATION
        WHERE STT_LOG_ID = #{sttLogId}

mapper.update:
		/* DictationMapper.update */
        UPDATE IAAP_STT_DICTATION
        SET
            USAGE_TYPE = #{usageType},
            DICTATED_TEXT = #{dictatedText}
        WHERE ID = #{id}

		/* DictationMapper.updateType */
        UPDATE IAAP_STT_DICTATION
        SET USAGE_TYPE = #{usageType}
        WHERE ID = #{id}

		/* DictationMapper.updatePreempt */
        UPDATE IAAP_STT_DICTATION
        SET IS_PREEMPTED = #{isPreempted}
        WHERE ID = #{id}

		/* DictationMapper.updateReUse */
        UPDATE IAAP_STT_DICTATION
        SET 
        	USE_YN = 'Y'
			, REG_DT = current_date
	        , REG_ID = #{regId}
	        , REG_IP = #{regIp}
	        , UPD_DT = current_date
	        , UPD_ID = #{updId}
	        , UPD_IP = #{updIp}
	        , TRANSCRIPT = #{transcript}
	        , DICTATED_TEXT = NULL 
        WHERE STT_LOG_ID = #{sttLogId}

		/* DictationMapper.delete */
        UPDATE IAAP_STT_DICTATION
        SET USE_YN = 'N'
        WHERE ID = #{id}

mapper.insert:
/* DictationMapper.confidenceSave */ MERGE INTO IAAP_STT_CONFIG ISC USING (
SELECT #{item.codeKey} as codeKey , #{item.codeValue} as codeValue , #{item.description} as description , #{item.regId} as regId , #{item.regIp} as regIp , #{item.updId} as updId , #{item.updIp} as updIp , #{item.useYn} as useYn FROM DUAL
) T1 ON (ISC.CODE_KEY = T1.codeKey AND ISC.DESCRIPTION = T1.description) WHEN MATCHED THEN UPDATE SET CODE_VALUE = T1.codeValue , UPD_DT = current_date , UPD_ID = T1.updId , UPD_IP = T1.updIp WHEN NOT MATCHED THEN INSERT( ID , CODE_KEY , CODE_VALUE , DESCRIPTION , REG_DT , REG_ID , REG_IP , UPD_DT , UPD_ID , UPD_IP , USE_YN ) VALUES( SEQ_IAAP_STT_CONFIG.NEXTVAL , T1.codeKey , T1.codeValue , T1.description , current_date , T1.regId , T1.regIp , current_date , T1.updId , T1.updIp , T1.useYn )
