mapper.select:
/* ServiceModelMapper.count */
SELECT COUNT(1)
FROM
IAAP_STT_SERVICE_MODEL SSM
WHERE 1 = 1 
	AND SSM.USE_YN = 'Y'

    AND SSM.SERVICE_MODEL_NAME = #{serviceModelName}


    AND SSM.SERVICE_CODE = #{serviceCode}


        AND SSM.REG_ID = #{uploadedBy}



        AND SSM.REG_DT >= to_date(#{from} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS')


        AND SSM.REG_DT >= to_date('2022-01-01 00:00:00', 'YYYY-MM-DD HH24:MI:SS')




           AND SSM.REG_DT <= to_date(#{to} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')


           AND SSM.REG_DT <= current_date

mapper.update:
/* ServiceModelMapper.update */
UPDATE
    IAAP_STT_SERVICE_MODEL
SET SERVICE_MODEL_NAME     = #{serviceModelName}
  , SERVICE_CODE           = #{serviceCode}
  , DESCRIPTION            = #{description}
  , UPD_DT                 = CURRENT_TIMESTAMP
  , UPD_ID                 = #{updId}
  , UPD_IP                 = #{updIp}
WHERE ID = #{id}
  AND USE_YN = 'Y'

mapper.select:
/* ServiceModelMapper.countDuplicateServiceModelName */
SELECT COUNT(1)
FROM IAAP_STT_SERVICE_MODEL SSM
WHERE SSM.SERVICE_MODEL_NAME = #{serviceModelName}
  AND USE_YN = 'Y'

/* ServiceModelMapper.countDuplicateServiceCode */
SELECT COUNT(1)
FROM IAAP_STT_SERVICE_MODEL SSM
WHERE SSM.SERVICE_CODE = #{serviceCode}
  AND USE_YN = 'Y'
